import Header from '../Components/Header'
import Button from '../Components/Button.jsx'
import InputData from '../Components/InputData'
import styles from '../Style/App_module.css'
import { redirect } from 'react-router-dom';
import { withAuth } from '../HOC/withAuth';
import { useContext, useState } from 'react';


function Timer() {

    const [inputHours, setInputHours] = useState('');
    const [inputMinutes, setInputMinutes] = useState('');
    const [inputSeconds, setInputSeconds] = useState('');

    let newTime = {
        Hours: inputHours,
        Minutes: inputMinutes,
        Seconds: inputSeconds
        };

        const Time = ({date}) => {
            const [finishTime] = useState(date.getTime());
            const [[diffDays, diffH, diffM, diffS], setDiff] = useState([0, 0, 0, 0]);
            const [tick, setTick] = useState(false);
        
            useEffect(()=> {
              const diff = (finishTime - new Date()) / 1000;
              if (diff < 0) return // время вышло
              setDiff([
                Math.floor(diff / 86400), // дни
                Math.floor((diff / 3600) % 24), 
                Math.floor((diff / 60) % 60), 
                Math.floor(diff % 60)
              ]) 
            }, [tick, finishTime])
                
            useEffect(()=>{
              const timerID = setInterval(() => setTick(!tick), 1000);
              return () => clearInterval(timerID);
            }, [tick])
        
            return ( 
              <p>{`${diffDays} дней ${diffH.toString().padStart(2, '0')}:${diffM
                  .toString()
                  .padStart(2, '0')}:${diffS.toString().padStart(2, '0')}`}</p>
            )
          }
        
  return (
    <div >
      <main>
        <p align="center">
            <input
            placeholder="Enter hours"
            value={inputHours} 
            title="H"
            onChange={(event) => setInputHours(event.target.value)} 
            />
            <input
            placeholder="Enter minutes"
            value={inputMinutes} 
            title="M"
            onChange={(event) => setInputMinutes(event.target.value)} 
            />
            <input
            placeholder="Enter seconds"
            value={inputSeconds} 
            title="S"
            onChange={(event) => setInputSeconds(event.target.value)} 
            />
            <button onClick={redirect("")}>
                Go
            </button>

        </p>
      </main>
    </div>
  )
}

export default Timer
