import Header from '../Components/Header'
import Button from '../Components/Button.jsx'
import InputData from '../Components/InputData'
import styles from '../Style/App_module.css'
import { redirect } from 'react-router-dom';
import { withAuth } from '../HOC/withAuth';
import { useContext, useState, useEffect } from 'react';


        const useCountdown = (onDone, initialSeconds,initialMinutes,initialHours) => {
            const [Hours, setHours] = useState(initialHours);
            const [Minutes, setMinutes] = useState(initialMinutes);
            const [seconds, setSeconds] = useState(initialSeconds);
            const [timeout, _setTimeout] = useState();
            const [render, rerender] = useState();
          
            const countDown = () => {
              setSeconds((prev) => prev - 1);
            };
          
            const runTimer = () => {
              if (seconds === 0) return onDone();
              const timer = setTimeout(() => {
                countDown();
              }, 1000);
              _setTimeout(timer);
            };
          
            const reset = () => {
              setSeconds(initialSeconds);
              clearTimeout(timeout);
              rerender({});
            };
          
            useEffect(() => {
              runTimer();
            }, [seconds, render]);
          
            return { seconds, reset };
          }
          
          export default function Timer() {
            const onDone = () => console.log("Timer counted down");
            const { seconds, reset } = useCountdown(onDone, 99);
          
            return (
              <div align="center">
                <p>Timer<br/>
                {seconds}</p>
                <input 
              placeholder="Enter Hours"
              value={inputPassword}
              title="Hours" 
              onChange={(event) => setInputPassword(event.target.value)}>
              </input>
              <input 
              placeholder="Enter Minutes"
              value={inputPassword}
              title="Minutes" 
              onChange={(event) => setInputPassword(event.target.value)}>
              </input>
              <input 
              placeholder="Enter Seconds"
              value={inputPassword}
              title="Seconds" 
              onChange={(event) => setInputPassword(event.target.value)}>
              </input>
                <button onClick={reset}>reset</button>
              </div>
            );
          }
